import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Pagination from '../components/homeComponents/pagination';
import Header from '../components/Header';
import UpdateProductForm from './UpdateProductForm';

const ProductListScreen = () => {
    const [products, setProducts] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(0);
    const pageSize = 6;
    const [updatingProduct, setUpdatingProduct] = useState(null);

    const handleUpdate = (product) => {
        // Ensure product has an id property before setting state
        if (product && product.id) {
            setUpdatingProduct(product);
        } else {
            console.error('Error: product does not have an id property');
        }
    };
    
    const handleUpdateSubmit = async (updatedProduct) => {
        try {
            // Check if updatedProduct has an id property
            if (!updatedProduct.id) {
                console.error('Error: updatedProduct does not have an id property');
                return;
            }
    
            const response = await axios.put(`/api/products/${updatedProduct.id}`, updatedProduct);
            if (response.status === 200) {
                setProducts(products.map(product => product.id === updatedProduct.id ? updatedProduct : product));
            } else {
                console.error('Error updating product:', response);
            }
        } catch (error) {
            console.error('Error updating product:', error);
        }
    };


    useEffect(() => {
        const fetchProducts = async () => {
            const { data } = await axios.get(`/api/products`);
            console.log(data);
            if (Array.isArray(data.products)) {
                setProducts(data.products);
            } else {
                console.error('Data.products is not an array:', data.products);
            }
        }
    
        fetchProducts();
    }, []);
    const handleDelete = async (id) => {
        try {
            await axios.delete(`/api/products/${id}`);
            setProducts(products.filter((product) => product._id !== id));
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div>
            <Header/>
            {products.map((product) => (
                <div key={product.id} style={cardStyle}>
                    <div style={contentStyle}>
                        <h2>{product.name}</h2>
                        <p>{product.description}</p>
                        <button onClick={() => handleDelete(product.id)}>Eliminar</button>
                        <button onClick={() => handleUpdate(product)}>Actualizar</button>
                    </div>
                    {updatingProduct === product && (
                        <UpdateProductForm product={product} onUpdate={handleUpdateSubmit} />
                    )}
                    <img src={product.image} alt={product.name} style={imageStyle} />
                </div>
            ))}
        </div>
    );
};


const cardStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    border: '1px solid #ccc',
    borderRadius: '4px',
    padding: '10px',
    margin: '10px',
};

const imageStyle = {
    maxWidth: '10%',
};

const contentStyle = {
    marginRight: '10px',
};



export default ProductListScreen;
