import React, { useEffect, useState } from "react";
import {Link, useParams} from "react-router-dom";
import Header from "./../components/Header";
import {PayPalButton} from "react-paypal-button-v2";
import { useDispatch, useSelector } from "react-redux";
import { getOrderDetails } from "../Redux/Actions/OrderActions";
import Loading from './../components/LoadingError/Loading';
import Message from './../components/LoadingError/Error';
import moment from 'moment';
import 'moment/locale/es';
import { payOrder } from '../Redux/Actions/OrderActions';
import axios from 'axios';

moment.locale('es');

const OrderScreen = () => {
    window.scrollTo(0, 0);
    const dispatch = useDispatch();
    const { id } = useParams();
    const orderId = id;
    const orderDetails = useSelector((state) => state.orderDetails);
    const {order,loading,error} = orderDetails;
    const [showPaymentForm, setShowPaymentForm] = useState(false);
    const [paymentResult, setPaymentResult] = useState(null);

    if (!loading) {
        const addDecimals = (num) => {
            return (Math.round(num * 100) / 100).toFixed(2);};
    
        order.itemsPrice = addDecimals(order.orderItems.reduce((acc, item) => acc + item.price * item.qty, 0));
    }

    useEffect(() => {
        dispatch(getOrderDetails(orderId,paymentResult));
    }, [dispatch, orderId, paymentResult]);

    const submitHandler = async (e) => {
        e.preventDefault();
        const paymentResult = {
            id: Math.floor(Math.random() * 1000000), // Genera un n√∫mero aleatorio entre 0 y 1000000
            status: 'Completado',
            update_time: new Date().toISOString(),
            payer: {
                email_address: order.user.email
            }
        };
        setPaymentResult(paymentResult);
        dispatch(payOrder(orderId, paymentResult));
    
        try {
            const response = await axios.post('/api/payments', paymentResult);
            console.log(response.data);
            const customerData = {
                address: order.shippingAddress.address,
                name: order.user.name,
                postalCode: order.shippingAddress.postalCode,
                city: order.shippingAddress.city, 
                country: order.shippingAddress.country,
                email: order.user.email
            };
            const customerResponse = await axios.post('/api/customers', customerData);
            console.log(customerResponse.data);
            // Post to the billing table
            const billingData = {
                total: order.totalPrice, 
                customerName: order.user.name,
                invoiceDate: new Date(), 
                productCount: order.orderItems.length,  
                customerAddress: order.shippingAddress.address
            };
        const billingResponse = await axios.post('/api/billing', billingData);
        console.log(billingResponse.data);
        } catch (error) {
            console.error(error);
        }
    };

    return(
        <>
            <Header />
            <div className="container">
                {
                    loading ? (<Loading/>) : error ? (<Message variant="alert-danger">{error}</Message>

                    ):
                    (
                        <>
                            <div className="row order-detail">
                                <div className="col-lg-4 col-sm-4 mb-lg-4 mb-5 mb-sm-0">
                                    <div className="row">
                                        <div className="col-md-4 center">
                                            <div className="alert-success order-box">
                                                <i className="fas fa-user"></i>
                                            </div>
                                        </div>
                                        <div className="col-md-8 center">
                                            <h5>
                                                <strong>Cliente</strong>
                                            </h5>
                                            <p>{order.user.name}</p>
                                            <p>
                                                <a href={`mailto:${order.user.email}`}>{order.user.email}</a>
                                            </p>
                                        </div>
                                    </div>
                                </div>
                                {/* 2 */}
                                <div className="col-lg-4 col-sm-4 mb-lg-4 mb-5 mb-sm-0">
                                    <div className="row">
                                        <div className="col-md-4 center">
                                            <div className="alert-success order-box">
                                                <i className="fas fa-truck-moving"></i>
                                            </div>
                                        </div>
                                        <div className="col-md-8 center">
                                            <h5>
                                                <strong>Informaci√≥n de orden</strong>
                                            </h5>
                                            <p>Envio: {order.shippingAddress.country}</p>
                                            <p>M√©todo de pago: {order.paymentMethod}</p>
                                            {
                                                order.isPaid ? (
                                                    <div className="bg-info p-2 col-12">
                                                        <p className="text-white text-center text-sm-start">
                                                            Pagado el {moment(order.paidAt).calendar()}
                                                        </p>
                                                    </div>
                                                ) : (
                                                    <div className="bg-danger p-2 col-12">
                                                        <p className="text-white text-center text-sm-start">
                                                            No pagado
                                                        </p>
                                                    </div>
                                                )
                                            }
                                        </div>
                                    </div>
                                </div>
                                {/* 3 */}
                                <div className="col-lg-4 col-sm-4 mb-lg-4 mb-5 mb-sm-0">
                                    <div className="row">
                                        <div className="col-md-4 center">
                                            <div className="alert-success order-box">
                                                <i className="fas fa-map-marker-alt"></i>
                                            </div>
                                        </div>
                                        <div className="col-md-8 center">
                                            <h5>
                                                <strong>Enviar a</strong>
                                            </h5>
                                            <p>Direcci√≥n: {order.shippingAddress.city}, {order.shippingAddress.address}, {order.shippingAddress.postalCode}</p>
                                            {
                                                order.isDelivered ? (
                                                    <div className="bg-info p-2 col-12">
                                                        <p className="text-white text-center text-sm-start">
                                                            Enviado el {moment(order.deliveredAt).calendar()}
                                                        </p>
                                                    </div>
                                                ) : (
                                                    <div className="bg-danger p-2 col-12">
                                                        <p className="text-white text-center text-sm-start">
                                                            No enviado
                                                        </p>
                                                    </div>
                                                )
                                            }
                                        </div>
                                    </div>
                                </div>
                            </div>

                            <div className="row order-products justify-content-between">
                                <div className="col-lg-8">
                                    {
                                        order.orderItems.length === 0 ? (
                                            <Message variant="alert-info mt-5">Tu orden est√° vacia</Message>
                                        ) : (
                                            <>
                                            {
                                                order.orderItems.map((item, index) => (
                                                    <div className="order-product row" key={index}>
                                                        <div className="col-md-3 col-6">
                                                            <img src={item.image} alt={item.name}  style={{width: '100px'}} />
                                                        </div>
                                                        <div className="col-md-5 col-6 d-flex align-items-center">
                                                            <Link to={`/products/${item.product}`}>
                                                                <h6>{item.name}</h6>
                                                            </Link>
                                                        </div>
                                                        <div className="mt-3 mt-md-0 col-md-2 col-6 d-flex align-items-center flex-column" >
                                                            <h4>Cantidad</h4>
                                                            <h6>{item.qty}</h6>
                                                        </div>
                                                        <div className="mt-3 mt-md-0 col-md-2 col-6 align-items-end d-flex flex-column">
                                                            <h4>Subtotal</h4>
                                                            <h6>${item.qty * item.price}</h6>
                                                        </div>
                                                    </div>
                                                ))
                                            }
                                            </>
                                        )
                                    }
                                </div>
                                {/*total*/}
                                <div className="col-lg-3 d-flex align-items-end flex-column mt-5 subtotal-order">
                                    <table className="table table-bordered">
                                        <tbody>
                                            <tr>
                                                <td>
                                                    <strong>Productos</strong>
                                                </td>
                                                <td>${order.itemsPrice}</td>
                                            </tr>
                                                <tr>
                                                <td>
                                                    <strong>Env√≠o</strong>
                                                </td>
                                                <td>${order.shippingPrice}</td>
                                            </tr>
                                            <tr>
                                                <td>
                                                    <strong>Total</strong>
                                                </td>
                                                <td>${order.totalPrice}</td>
                                            </tr>
                                        </tbody>
                                    </table>
                                    <div className="col-12">
                                        <button disabled={order.isPaid} onClick={() => setShowPaymentForm(true)}>Pagar</button>
                                        {showPaymentForm && (
                                            <form onSubmit={submitHandler}>
                                                <label>
                                                    N√∫mero de tarjeta:
                                                    <input type="text" name="cardNumber" required disabled={order.isPaid}/>
                                                </label>
                                                <label>
                                                    Fecha de vencimiento:
                                                    <input type="text" name="expiryDate" pattern="(0[1-9]|1[0-2])\/\d{2}" required disabled={order.isPaid}/>
                                                </label>
                                                <label>
                                                    CVV:
                                                    <input type="text" name="cvv" pattern="\d{3}" required disabled={order.isPaid}/>
                                                </label>
                                                <label>
                                                    Nombre del titular:
                                                    <input type="text" name="cardHolderName" required disabled={order.isPaid} />
                                                </label>
                                                <input type="submit" value="Submit" disabled={order.isPaid} />
                                            </form>
                                        )}
                                    </div>
                                </div>
                            </div>
                        </>
                    )
                }
            </div>
        </>
    );
};

export default OrderScreen;

