import React, { useEffect, useState} from "react";
import { Link, useParams} from "react-router-dom";
import { useNavigate } from 'react-router-dom';
import Header from "./../components/Header";
import Rating from "./../components/homeComponents/Rating";
import Message from "./../components/LoadingError/Error.js";
import axios from "axios";
import { listProductDetails } from "../Redux/Actions/ProductActions";
import { useDispatch, useSelector } from "react-redux";
import Loading from "./../components/LoadingError/Loading.js";
import { addToCart } from "../Redux/Actions/CartActions";

const SingleProduct = () => {
    const navigate = useNavigate();
    const { id: productId } = useParams();
    const dispatch = useDispatch();
    const[qty, setQty] = useState(1);
    const[rating, setRating] = useState(0);
    const[comment, setComment] = useState("");

    const productDetails = useSelector((state) => state.productDetails);
    const{loading,error,product} = productDetails;

    const userLogin = useSelector((state) => state.userLogin);
    const{userInfo} = userLogin;

    useEffect(() => {
        dispatch(listProductDetails(productId));
    }, [dispatch, productId]);

    const AddToCartHandler = (e) => {
        e.preventDefault();
        dispatch(addToCart(productId, qty));
        navigate(`/cart/${productId}?qty=${qty}`);
    }

    return (
        <>
            <Header />
            <div className="container single-product">
                {
                    loading ? (
                        <Loading/> ) : error ? ( <Message variant= "alert-danger">{error}</Message> )
                        : (
                            <>
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="single-image">
                                            <img src={product.image} alt={product.name} />
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="product-dtl">
                                            <div className="product-info">
                                                <div className="product-name">{product.name}</div>
                                            </div>
                                            <p>{product.description}</p>

                                        <div className="product-count col-lg-7">
                                            <div className="flex-box d-flex justify-content-between align-items-center">
                                                <h6>Precio</h6>
                                                <span>{product.price}</span>
                                            </div>
                                            <div className="flex-box d-flex justify-content-between align-items-center">
                                                <h6>Estado</h6>
                                                {product.countInStock > 0 ? (<span>En Stock</span>) : (<span>No disponible</span>)}
                                            </div>
                                            <div className="flex-box d-flex justify-content-between align-items-center">
                                                <h6>ReseÃ±as</h6>
                                                <Rating
                                                    value={product.rating}
                                                    text={`${product.numReviews} reseÃ±as`}
                                                />
                                            </div>
                                            {product.countInStock > 0 ? (
                                                <>
                                                    <div className="flex-box d-flex justify-content-between align-items-center">
                                                        <h6>Cantidad</h6>
                                                        <select value={qty} onChange={(e) => setQty(e.target.value)}>
                                                            {[...Array(product.countInStock).keys()].map((x) => (
                                                                <option key={x + 1} value={x + 1}>
                                                                    {x + 1}
                                                                </option>
                                                            ))}
                                                        </select>
                                                    </div>
                                                    <button onClick={AddToCartHandler} className="round-black-btn">Agregar al carrito</button>
                                                </>
                                            ) : null}
                                        </div>
                                    </div>
                                </div>
                                </div>

                                {/* RATING*/}

                                <div className="row my-5">
                                    <div className="col-md-6">
                                        <h6 className="mb-3">ReseÃ±as</h6>
                                        <Message variant={"alert-info mt-3"}>No hay reseÃ±as</Message>
                                        <div className="mb-5 mb-md-3 bg-light p-3 shadow-sm rounded">
                                            <strong>Administrador</strong>
                                            <Rating />
                                            <span>15 de Enero 2021</span>
                                            <div className="alert alert-info mt-3">
                                                Lorem ipsum is simply dummy text of the printing and typesetting industry.    
                                            </div>
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <h6>EscribÃ­ una reseÃ±a</h6>
                                        <div className="my-4"></div>
                                        <form>
                                            <div className="my-4">
                                                <strong>CalificaciÃ³n</strong>
                                                <select className="col-12 bg-light p-3 mt-2 border-0 rounded">
                                                    <option value="">Seleccionar...</option>
                                                    <option value="1">1 - PÃ©simo</option>
                                                    <option value="2">2 - Regular</option>
                                                    <option value="3">3 - Bueno</option>
                                                    <option value="4">4 - Muy bueno</option>
                                                    <option value="5">5 - Excelente</option>
                                                </select>
                                            </div>
                                            <div className="my-4">
                                                <strong>Comentario</strong>
                                                <textarea 
                                                row="3"
                                                className="col-12 bg-light p-3 mt-2 border-0 rounded" 
                                                ></textarea>
                                            </div>
                                            <div className="my-3">
                                                <button className="col-12 bg-black border-0 p-3 rounded text-white">Enviar</button>
                                            </div>
                                        </form>
                                        <div className="my-3">
                                            <Message variant={"alert-warning"}>
                                                Por favor {" "}
                                                <Link to="/login">
                                                    " <strong>Inicia sesiÃ³n</strong> "
                                                </Link> {" "}
                                                para escribir una reseÃ±a{" "}
                                            </Message>
                                        </div>
                                    </div>
                                </div>
                            </>
                            )
                }
            </div>
        </>
    );
};

export default SingleProduct;
