import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Pagination from '../components/homeComponents/pagination';

const ProductListScreen = () => {
    const [products, setProducts] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(0);
    const pageSize = 6;

    useEffect(() => {
        const fetchAllProducts = async () => {
            let page = 1;
            let fetchedProducts = [];
            while (true) {
                const { data } = await axios.get(`/api/products?page=${page}&limit=${pageSize}`);
                if (data.products.length === 0) {
                    break;
                }
                fetchedProducts = fetchedProducts.concat(data.products);
                page++;
            }
            setProducts(fetchedProducts);
        }
    
        fetchAllProducts();
    }, []);
    const handleDelete = async (id) => {
        try {
            await axios.delete(`/api/products/${id}`);
            setProducts(products.filter((product) => product._id !== id));
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div>
            <h1>Productos</h1>
            {products.map((product) => (
                <div key={product._id}>
                    <h2>{product.name}</h2>
                    <button onClick={() => handleDelete(product._id)}>Eliminar</button>
                    <button onClick={() => {/* Aquí puedes manejar la actualización del producto */}}>Actualizar</button>
                </div>
            ))}
            <Pagination page={currentPage} pages={totalPages} />
        </div>
    );
};

export default ProductListScreen;
