import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

function ProductListScreen() {
  const [products, setProducts] = useState([]);
  const history = useHistory();

  useEffect(() => {
    const fetchProducts = async () => {
      const { data } = await axios.get(`/api/products`);
      if (Array.isArray(data.products)) {
        setProducts(data.products);
      } else {
        console.error('Data.products is not an array:', data.products);
      }
    }
    fetchProducts();
  }, []);

  const handleUpdate = (product) => {
    // Aquí puedes redirigir al usuario a la pantalla de actualización de producto
    // con el producto como parámetro
    history.push(`/update-product/${product._id}`);
  }

  return (
    <div>
      {products.map(product => (
        <div key={product._id}>
          <h2>{product.name}</h2>
          <button onClick={() => handleUpdate(product)}>Actualizar</button>
        </div>
      ))}
    </div>
  );
}


const cardStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    border: '1px solid #ccc',
    borderRadius: '4px',
    padding: '10px',
    margin: '10px',
};

const imageStyle = {
    maxWidth: '10%',
};

const contentStyle = {
    marginRight: '10px',
};



export default ProductListScreen;
