import React, { useEffect, useState } from 'react';
import axios from 'axios';


const ProductListScreen = () => {
    const [products, setProducts] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const pageSize = 6;

    useEffect(() => {
        const fetchProducts = async () => {
            const { data } = await axios.get('/api/products');
            console.log(data);
            if (Array.isArray(data.products)) {
                setProducts(data.products);
            } else {
                console.error('Data.products is not an array:', data.products);
            }
        }
    
        fetchProducts();
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`/api/products/${id}`);
            setProducts(products.filter((product) => product._id !== id));
        } catch (error) {
            console.error(error);
        }
    }

    // Calcula los productos que se deben mostrar en la página actual
    const productsToShow = products.slice((currentPage - 1) * pageSize, currentPage * pageSize);

    return (
        <div>
            {productsToShow.map(product => (
                <div key={product._id}>{product.name}</div>
            ))}
            <button onClick={() => setCurrentPage(currentPage - 1)} disabled={currentPage === 1}>Previous Page</button>
            <button onClick={() => setCurrentPage(currentPage + 1)} disabled={currentPage === Math.ceil(products.length / pageSize)}>Next Page</button>
        </div>
    );
}

export default ProductListScreen;
